#include <stdint.h>


int main(void)
{
	//Address pointer definitions
	uint32_t *pClkCtrlReg = (uint32_t *)0x40023830;
	//GPIOD
	uint32_t *pPDModeReg  = (uint32_t *)0x40020C00;
	uint32_t *pPDOutReg   = (uint32_t *)0x40020C14;
	//GPIOA
	uint32_t *pPAModeReg  = (uint32_t *)0x40020000;
	uint32_t *pPAInReg 	  = (uint32_t *)0x40020010;

	//1. Enable AHB1ENR clock for GPIOD and GPIOA peripheral
	*pClkCtrlReg |= (9 << 0); // Enable clock for PD and PA

	//2. Set GPIOD MODE to output for PD12
	*pPDModeReg &= ~(0xC0000000 >> 6); //clearing
	*pPDModeReg |= (0x80000000 >> 7); //setting

	//3. Set GPIOA MODE to input for PA0
	*pPAModeReg &= ~(3 << 0); //clearing and setting of PA mode Register's 1st and 2nd bits


    /* Loop forever */
	for(;;){
		if ( *pPAInReg & (1 << 0) ){
			//VDD connected to PA0
			*pPDOutReg |= (1 << 12); //HIGH to PD12 led4
		}else{
			//GND connected to PA0
			*pPDOutReg &= ~(1 << 12); //LOW to PD12 led4
		}



	}
}
